CREATE TABLE USER (ID BIGINT NOT NULL, DTYPE VARCHAR(31), PASSWORD VARCHAR(255), USERNAME VARCHAR(255), ADDRESS VARCHAR(255), CITY VARCHAR(255), COUNTRY VARCHAR(255), FIRSTNAME VARCHAR(255), GENDER INTEGER, LASTNAME VARCHAR(255), PHONENUMBER VARCHAR(255), ZIPCODE INTEGER, PRIMARY KEY (ID))
CREATE TABLE SPONSOREDCHILD (ID BIGINT NOT NULL, ADDRESS VARCHAR(255), CITY VARCHAR(255), COUNTRY VARCHAR(255), FIRSTNAME VARCHAR(255), GENDER INTEGER, LASTNAME VARCHAR(255), PRIMARY KEY (ID))
CREATE TABLE PAYMENT (ID BIGINT NOT NULL, AMOUNT INTEGER, CONCEPT VARCHAR(255), PAYMENTMETHOD VARCHAR(255), PAYMENTTIMESTAMP TIMESTAMP, PRIMARY KEY (ID))
CREATE TABLE USER_PAYMENT (Sponsor_ID BIGINT NOT NULL, payments_ID BIGINT NOT NULL, PRIMARY KEY (Sponsor_ID, payments_ID))
CREATE TABLE USER_SPONSOREDCHILD (Sponsor_ID BIGINT NOT NULL, sponsoredChildren_ID BIGINT NOT NULL, PRIMARY KEY (Sponsor_ID, sponsoredChildren_ID))
ALTER TABLE USER_PAYMENT ADD CONSTRAINT SRPAYMENTpymentsID FOREIGN KEY (payments_ID) REFERENCES PAYMENT (ID)
ALTER TABLE USER_PAYMENT ADD CONSTRAINT SRPAYMENTSponsorID FOREIGN KEY (Sponsor_ID) REFERENCES USER (ID)
ALTER TABLE USER_SPONSOREDCHILD ADD CONSTRAINT SRSPNspnsrdChldrnD FOREIGN KEY (sponsoredChildren_ID) REFERENCES SPONSOREDCHILD (ID)
ALTER TABLE USER_SPONSOREDCHILD ADD CONSTRAINT SRSPNSRDCHLDSpnsrD FOREIGN KEY (Sponsor_ID) REFERENCES USER (ID)
CREATE TABLE SEQUENCE (SEQ_NAME VARCHAR(50) NOT NULL, SEQ_COUNT DECIMAL(15), PRIMARY KEY (SEQ_NAME))
